    private void btnConfirmarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConfirmarActionPerformed

        String usuario = campoUsuario.getText();
        String senha = campoSenha.getText();

        // instanciando objeot para chamar o metodo que ira verifiar as credenciais
        Login_Controller verificaPermissoes = new Login_Controller();
        
        // o metodo retorna um boolean que indica se foi autorizado o acesso
        // caso true fecha a tela de login e abre a tela principal
        if (verificaPermissoes.Login_Controller(usuario, senha)) {
            dispose();
        }
    }//GEN-LAST:event_btnConfirmarActionPerformed

