    private void campoUsuarioKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_campoUsuarioKeyPressed
        // capturando o evento, logo apos verificando se é ENTER
        // passando o foco para outro campo        
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            campoSenha.requestFocus();
        }
    }//GEN-LAST:event_campoUsuarioKeyPressed

    private void campoSenhaKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_campoSenhaKeyPressed
        // TODO add your handling code here:

        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            btnConfirmar.requestFocus();
        }
    }//GEN-LAST:event_campoSenhaKeyPressed

    private void btnConfirmarKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_btnConfirmarKeyPressed
        // tecla ENTER
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            String usuario = campoUsuario.getText();
            String senha = campoSenha.getText();

            Login_Controller verificaPermissoes = new Login_Controller();
            if (verificaPermissoes.Login_Controller(usuario, senha)) {
                dispose();
            }
        }
    }//GEN-LAST:event_btnConfirmarKeyPressed

